ContTables <- function(
    data,
    rows,
    cols,
    counts = NULL,
    layers = list(),
    chiSq = TRUE,
    chiSqCorr = FALSE,
    likeRat = FALSE,
    contCoef = FALSE,
    phiCra = FALSE,
    logOdds = FALSE,
    ciWidth = 95,
    gamma = FALSE,
    taub = FALSE,
    obs = TRUE,
    exp = FALSE,
    pcRow = FALSE,
    pcCol = FALSE,
    pcTot = FALSE,
    rowOrder = "asc",
    colOrder = "asc") {

    options <- ContTablesOptions$new(
        rows = rows,
        cols = cols,
        counts = counts,
        layers = layers,
        chiSq = chiSq,
        chiSqCorr = chiSqCorr,
        likeRat = likeRat,
        contCoef = contCoef,
        phiCra = phiCra,
        logOdds = logOdds,
        ciWidth = ciWidth,
        gamma = gamma,
        taub = taub,
        obs = obs,
        exp = exp,
        pcRow = pcRow,
        pcCol = pcCol,
        pcTot = pcTot,
        rowOrder = rowOrder,
        colOrder = colOrder)

    results <- ContTablesResults$new(
        options = options)

    analysis <- ContTablesClass$new(
        options = options,
        data = data)

    analysis$run()
    analysis$render()

    analysis
}
