Descriptives <- function(
    data,
    vars,
    mean = TRUE,
    median = TRUE,
    mode = FALSE,
    sum = FALSE,
    sd = FALSE,
    variance = FALSE,
    range = FALSE,
    min = TRUE,
    max = TRUE,
    se = FALSE,
    skew = FALSE,
    kurt = FALSE,
    freq = FALSE,
    plots = FALSE,
    plotW = 480,
    plotH = 320,
    quart = FALSE,
    pcEqGr = FALSE,
    pcNEqGr = 4) {

    options <- DescriptivesOptions$new(
        vars = vars,
        mean = mean,
        median = median,
        mode = mode,
        sum = sum,
        sd = sd,
        variance = variance,
        range = range,
        min = min,
        max = max,
        se = se,
        skew = skew,
        kurt = kurt,
        freq = freq,
        plots = plots,
        plotW = plotW,
        plotH = plotH,
        quart = quart,
        pcEqGr = pcEqGr,
        pcNEqGr = pcNEqGr)

    results <- DescriptivesResults$new(
        options = options)

    analysis <- DescriptivesClass$new(
        options = options,
        data = data)

    analysis$run()
    analysis$render()

    analysis
}
