Kruskal <- function(
    data,
    deps,
    group,
    pairs = FALSE) {

    options <- KruskalOptions$new(
        deps = deps,
        group = group,
        pairs = pairs)

    results <- KruskalResults$new(
        options = options)

    analysis <- KruskalClass$new(
        options = options,
        data = data)

    analysis$run()
    analysis$render()

    analysis
}
